    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String OOC=txtooc.getText();
        String SR=txtsr.getText();
        String DE=txtdel.getText();
        String AD=txtad.getText();
        String ADL=txtadl.getText();
        double ooc = Double.parseDouble(OOC);
        double sr = Double.parseDouble(SR);
        double de = Double.parseDouble(DE);
        double ad = Double.parseDouble(AD);
        double adl = Double.parseDouble(ADL);
        double total=(ooc+sr+de+ad+adl)/5;
       lbltgpa.setText(String.valueOf(total));
    }//GEN-LAST:event_jButton1ActionPerformed

