    private void btnmarksActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnmarksActionPerformed
        String quiz1 = txtq1.getText();
        String quiz2 = txtq2.getText();
        String quiz3 = txtq3.getText();
        double q1Marks = Double.parseDouble(quiz1);
        double q2Marks = Double.parseDouble(quiz2);
        double q3Marks = Double.parseDouble(quiz3);
        String p = txtpresentation.getText();
        String a = txtass.getText();
        String m = txtmid.getText();
        String f = txtfinal.getText();
        String at=txtattendance.getText();

        double presentation = Double.parseDouble(p);
        double assignment = Double.parseDouble(a);
        double mid = Double.parseDouble(m);
        double fnl = Double.parseDouble(f);
        double attendance=Double.parseDouble(at);
        double ttl = (((q1Marks + q2Marks + q3Marks) / 3) + presentation + assignment + mid + fnl+attendance);
        lbltotal.setText(String.valueOf(ttl));

    }//GEN-LAST:event_btnmarksActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String quiz1 = txtq1.getText();
        String quiz2 = txtq2.getText();
        String quiz3 = txtq3.getText();
        double q1Marks = Double.parseDouble(quiz1);
        double q2Marks = Double.parseDouble(quiz2);
        double q3Marks = Double.parseDouble(quiz3);
        double quiz = (q1Marks + q2Marks + q3Marks) / 3;
        lblquiz.setText(String.valueOf(quiz));
    }//GEN-LAST:event_jButton1ActionPerformed

